namespace CleanArchitecture.Blazor.Application.Features.CrmProductCategories.Specifications;
#nullable disable warnings
/// <summary>
/// Specification class for advanced filtering of CrmProductCategories.
/// </summary>
public class CrmProductCategoryAdvancedSpecification : Specification<CrmProductCategory>
{
    public CrmProductCategoryAdvancedSpecification(CrmProductCategoryAdvancedFilter filter)
    {
        DateTime today = DateTime.UtcNow;
        var todayrange = today.GetDateRange(CrmProductCategoryListView.TODAY.ToString(), filter.LocalTimezoneOffset);
        var last30daysrange = today.GetDateRange(CrmProductCategoryListView.LAST_30_DAYS.ToString(),filter.LocalTimezoneOffset);

        Query.Where(q => q.CategoryName != null)
             .Where(filter.Keyword,!string.IsNullOrEmpty(filter.Keyword))
             .Where(q => q.CreatedBy == filter.CurrentUser.UserId, filter.ListView == CrmProductCategoryListView.My && filter.CurrentUser is not null)
             .Where(x => x.Created >= todayrange.Start && x.Created < todayrange.End.AddDays(1), filter.ListView == CrmProductCategoryListView.TODAY)
             .Where(x => x.Created >= last30daysrange.Start, filter.ListView == CrmProductCategoryListView.LAST_30_DAYS);
       
    }
}
