// Usage:
// This command can be used to delete multiple SubProducts from the system by specifying
// their IDs. The handler also raises domain events for each deleted subproduct to
// notify other bounded contexts and invalidate relevant cache entries.

using CleanArchitecture.Blazor.Application.Features.SubProducts.Caching;


namespace CleanArchitecture.Blazor.Application.Features.SubProducts.Commands.Delete;

public class DeleteSubProductCommand:  ICacheInvalidatorRequest<Result<int>>
{
  public int[] Id {  get; }
  public string CacheKey => SubProductCacheKey.GetAllCacheKey;
  public IEnumerable<string>? Tags => SubProductCacheKey.Tags;
  public DeleteSubProductCommand(int[] id)
  {
    Id = id;
  }
}

public class DeleteSubProductCommandHandler : 
             IRequestHandler<DeleteSubProductCommand, Result<int>>

{
    private readonly IApplicationDbContext _context;
    public DeleteSubProductCommandHandler(
        IApplicationDbContext context)
    {
        _context = context;
    }
    public async Task<Result<int>> Handle(DeleteSubProductCommand request, CancellationToken cancellationToken)
    {
        var items = await _context.SubProducts.Where(x=>request.Id.Contains(x.Id)).ToListAsync(cancellationToken);
        foreach (var item in items)
        {
		    // raise a delete domain event
			//item.AddDomainEvent(new SubProductDeletedEvent(item));
            _context.SubProducts.Remove(item);
        }
        var result = await _context.SaveChangesAsync(cancellationToken);
        return await Result<int>.SuccessAsync(result);
    }

}

