// Usage:
// This validator ensures that the DeleteSubProductCommand is valid before attempting 
// to delete subproduct records from the system. It verifies that the ID list is not 
// null and that all IDs are greater than zero.

namespace CleanArchitecture.Blazor.Application.Features.SubProducts.Commands.Delete;

public class DeleteSubProductCommandValidator : AbstractValidator<DeleteSubProductCommand>
{
        public DeleteSubProductCommandValidator()
